import React, { useEffect, useState } from 'react';
import { Row, Col, Button, Form } from 'react-bootstrap';
import salesData from '../salesData.json';
import './SalesInsurancePage.css';
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

const COLORS = ["#8884d8", "#82ca9d", "#FFBB28", "#FF8042", "#AF19FF"];
const PIEDATA = [
   {
      name: "Apple",
      value: 54.85
   },
   {
      name: "Samsung",
      value: 47.91
   },
   {
      name: "Redmi",
      value: 16.85
   },
   {
      name: "One Plus",
      value: 16.14
   },
   {
      name: "Others",
      value: 10.25
   }
];

const CustomTooltip = ({ active, payload, label }) => {
  if (active) {
     return (
      <div
          className="custom-tooltip"
          style={{
            backgroundColor: "#ffff",
            padding: "5px",
            border: "1px solid #cccc"
          }}
      >
          <label>{`${payload[0].name} : ${payload[0].value}%`}</label>
      </div>
    );
  }
  return null;
};

export default function SalesInsurancePage() {
  const [sales, setSales] = useState(salesData);
  const [products, setProducts] = useState();

  useEffect(() => {
    if (!products) {
      fetch('https://phone-specs-api.azharimm.dev/latest')
        .then(async (data) => setProducts(await data.json()));
    }
  }, [products]);

  const [validated, setValidated] = useState(false);

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }

    setValidated(true);
  };

  const deleteSale = (saleToDelete) => {
    setSales((current) =>
      current.filter((sale) => saleToDelete.id !== sale.id)
    );
  };

  return (
    <div>      
      <div className="row">
        <div className="col-md-4 order-md-2 mb-4">
          <h4 className="d-flex justify-content-between align-items-center mb-3">
              <span className="text-muted">Sales Statistics</span>
          </h4>
          <div className="list-group mb-3 sticky-top">
            Add chart/pie here!
            <PieChart width={730} height={300}>
              <Pie
                data={PIEDATA}
                color="#000000"
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={120}
                fill="#8884d8"
              >
                {PIEDATA.map((entry, index) => (
                    <Cell
                      key={`cell-${index}`}
                      fill={COLORS[index % this.COLORS.length]}
                    />
                ))}
              </Pie>
              <Tooltip content={<CustomTooltip />} />
              <Legend />
            </PieChart>
          </div>
        </div>
        <div className="col-md-8 order-md-1">
          <h4 className="mb-3 text-muted">Insurance Sale</h4>
          <Form noValidate validated={validated} onSubmit={handleSubmit}>
            <Row className="mb-3">
              <Form.Group as={Col} controlId="formFirstName">
                <Form.Label>First Name</Form.Label>
                <Form.Control type="text" placeholder="Enter first name" required />
              </Form.Group>
              <Form.Group as={Col} controlId="formLastName">
                <Form.Label>Last Name</Form.Label>
                <Form.Control type="text" placeholder="Enter last name" required />
              </Form.Group>
            </Row>
            <Row className="mb-3">
              <Form.Group as={Col} controlId="formEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="Enter email" required />
              </Form.Group>
              <Form.Group as={Col} controlId="formAge">
                <Form.Label>Age</Form.Label>
                <Form.Control type="number" placeholder="Enter age" required />
              </Form.Group>
            </Row>
            <Row className="mb-3">    
              <Col sm={6}>
                <Form.Group as={Col} controlId="formDevice">
                  <Form.Label>Device</Form.Label>
                  <Form.Select defaultValue="Choose...">
                    <option>Choose...</option>
                    {products && products.data.phones.map((device) => (
                      <option key={device.slug}>{device.phone_name}</option>
                    ))}
                  </Form.Select>
                </Form.Group>
              </Col>  
              <Col xs="auto" className="mt-auto">
                <Button type="submit">Submit</Button>
              </Col>
            </Row>
          </Form>
        </div>
      </div>
      <hr className="mb-4"/>
      <div className="row">
        <div className="col-md-12 order-md-2 mb-4">
          <h4 className="d-flex justify-content-between align-items-center mb-3">
              <span className="text-muted">Insurance Sales</span>
              <span className="badge badge-secondary badge-pill">3</span>
          </h4>
          <div className="list-group mb-3">
              {sales && sales.map((sale) => (
                <div key={sale.id} className="list-group-item d-flex justify-content-between lh-condensed">
                  <div>
                      <h6 className="my-0">{sale.first_name} {sale.last_name} (Age: {sale.age})</h6>
                      <small className="text-muted">Device: {sale.product}</small>
                  </div>
                  <div>
                    <Button className="font-weight-bold">Update</Button>
                    <Button
                      className="font-weight-bold deleteButton"
                      onClick={() => deleteSale(sale)}
                    >
                      Delete
                    </Button>
                  </div>
                </div>
              ))}
              {sales.length === 0 && (<h6 className="mb-3 text-muted">No insurance sales so far...</h6>)}
          </div>
        </div>
      </div>
    </div>
  );
}
